package openwrap

import (
	"context"
	"net/http"
	"testing"

	"github.com/prebid/prebid-server/hooks/hookstage"
	"github.com/prebid/prebid-server/modules/pubmatic/openwrap/cache"
	"github.com/prebid/prebid-server/modules/pubmatic/openwrap/config"
	"github.com/prebid/prebid-server/modules/pubmatic/openwrap/models"
	"github.com/prebid/prebid-server/modules/pubmatic/openwrap/models/nbr"
	"github.com/stretchr/testify/assert"
)

func TestOpenWrap_handleEntrypointHook(t *testing.T) {
	type fields struct {
		cfg   config.Config
		cache cache.Cache
	}
	type args struct {
		in0     context.Context
		miCtx   hookstage.ModuleInvocationContext
		payload hookstage.EntrypointPayload
	}
	tests := []struct {
		name    string
		fields  fields
		args    args
		want    hookstage.HookResult[hookstage.EntrypointPayload]
		wantErr error
	}{
		{
			name: "valid /openrtb/2.5 request",
			fields: fields{
				cfg: config.Config{
					Tracker: config.Tracker{
						Endpoint:                  "t.pubmatic.com",
						VideoErrorTrackerEndpoint: "t.pubmatic.com/error",
					},
				},
				cache: nil,
			},
			args: args{
				in0:   context.Background(),
				miCtx: hookstage.ModuleInvocationContext{},
				payload: hookstage.EntrypointPayload{
					Request: func() *http.Request {
						r, err := http.NewRequest("POST", "http://localhost/openrtb/2.5?debug=1", nil)
						if err != nil {
							panic(err)
						}
						r.Header.Add("User-Agent", "go-test")
						r.Header.Add("SOURCE_IP", "127.0.0.1")
						r.Header.Add("Cookie", `KADUSERCOOKIE=7D75D25F-FAC9-443D-B2D1-B17FEE11E027; DPSync3=1684886400%3A248%7C1685491200%3A245_226_201; KRTBCOOKIE_80=16514-CAESEMih0bN7ISRdZT8xX8LXzEw&KRTB&22987-CAESEMih0bN7ISRdZT8xX8LXzEw&KRTB&23025-CAESEMih0bN7ISRdZT8xX8LXzEw&KRTB&23386-CAESEMih0bN7ISRdZT8xX8LXzEw; KRTBCOOKIE_377=6810-59dc50c9-d658-44ce-b442-5a1f344d97c0&KRTB&22918-59dc50c9-d658-44ce-b442-5a1f344d97c0&KRTB&23031-59dc50c9-d658-44ce-b442-5a1f344d97c0; ; KRTBCOOKIE_153=1923-LZw-Ny-bMjI2m2Q8IpsrNnnIYzw2yjdnLJsrdYse&KRTB&19420-LZw-Ny-bMjI2m2Q8IpsrNnnIYzw2yjdnLJsrdYse&KRTB&22979-LZw-Ny-bMjI2m2Q8IpsrNnnIYzw2yjdnLJsrdYse&KRTB&23462-LZw-Ny-bMjI2m2Q8IpsrNnnIYzw2yjdnLJsrdYse; KRTBCOOKIE_57=22776-41928985301451193&KRTB&23339-41928985301451193; KRTBCOOKIE_27=16735-uid:3cab6283-4546-4500-a7b6-40ef605fe745&KRTB&16736-uid:3cab6283-4546-4500-a7b6-40ef605fe745&KRTB&23019-uid:3cab6283-4546-4500-a7b6-40ef605fe745&KRTB&23114-uid:3cab6283-4546-4500-a7b6-40ef605fe745; KRTBCOOKIE_18=22947-1978557989514665832; KRTBCOOKIE_466=16530-4fc36250-d852-459c-8772-7356de17ab97; KRTBCOOKIE_391=22924-8044608333778839078&KRTB&23263-8044608333778839078&KRTB&23481-8044608333778839078; KRTBCOOKIE_1310=23431-b81c3g7dr67i&KRTB&23446-b81c3g7dr67i&KRTB&23465-b81c3g7dr67i; KRTBCOOKIE_1290=23368-vkf3yv9lbbl; KRTBCOOKIE_22=14911-4554572065121110164&KRTB&23150-4554572065121110164; KRTBCOOKIE_860=16335-YGAqDU1zUTdjyAFxCoe3kctlNPo&KRTB&23334-YGAqDU1zUTdjyAFxCoe3kctlNPo&KRTB&23417-YGAqDU1zUTdjyAFxCoe3kctlNPo&KRTB&23426-YGAqDU1zUTdjyAFxCoe3kctlNPo; KRTBCOOKIE_904=16787-KwJwE7NkCZClNJRysN2iYg; KRTBCOOKIE_1159=23138-5545f53f3d6e4ec199d8ed627ff026f3&KRTB&23328-5545f53f3d6e4ec199d8ed627ff026f3&KRTB&23427-5545f53f3d6e4ec199d8ed627ff026f3&KRTB&23445-5545f53f3d6e4ec199d8ed627ff026f3; KRTBCOOKIE_32=11175-AQEI_1QecY2ESAIjEW6KAQEBAQE&KRTB&22713-AQEI_1QecY2ESAIjEW6KAQEBAQE&KRTB&22715-AQEI_1QecY2ESAIjEW6KAQEBAQE; SyncRTB3=1685577600%3A35%7C1685491200%3A107_21_71_56_204_247_165_231_233_179_22_209_54_254_238_96_99_220_7_214_13_3_8_234_176_46_5%7C1684886400%3A2_223_15%7C1689465600%3A69%7C1685145600%3A63; KRTBCOOKIE_107=1471-uid:EK38R0PM1NQR0H5&KRTB&23421-uid:EK38R0PM1NQR0H5; KRTBCOOKIE_594=17105-RX-447a6332-530e-456a-97f4-3f0fd1ed48c9-004&KRTB&17107-RX-447a6332-530e-456a-97f4-3f0fd1ed48c9-004; SPugT=1684310122; chkChromeAb67Sec=133; KRTBCOOKIE_699=22727-AAFy2k7FBosAAEasbJoXnw; PugT=1684310473; origin=go-test`)
						return r
					}(),
					Body: []byte(`{"ext":{"wrapper":{"profileid":5890,"versionid":1}}}`),
				},
			},
			want: hookstage.HookResult[hookstage.EntrypointPayload]{
				ModuleContext: hookstage.ModuleContext{
					"rctx": models.RequestCtx{
						ProfileID:                 5890,
						DisplayID:                 1,
						SSAuction:                 -1,
						Debug:                     true,
						UA:                        "go-test",
						IP:                        "127.0.0.1",
						IsCTVRequest:              false,
						TrackerEndpoint:           "t.pubmatic.com",
						VideoErrorTrackerEndpoint: "t.pubmatic.com/error",
						UidCookie: &http.Cookie{
							Name:  "uids",
							Value: ``,
						},
						KADUSERCookie: &http.Cookie{
							Name:  "KADUSERCOOKIE",
							Value: `7D75D25F-FAC9-443D-B2D1-B17FEE11E027`,
						},
						OriginCookie:             "go-test",
						Aliases:                  make(map[string]string),
						ImpBidCtx:                make(map[string]models.ImpCtx),
						PrebidBidderCode:         make(map[string]string),
						BidderResponseTimeMillis: make(map[string]int),
					},
				},
			},
			wantErr: nil,
		},
		{
			name: "valid /openrtb/2.5 request with wiid set and no cookies",
			fields: fields{
				cfg: config.Config{
					Tracker: config.Tracker{
						Endpoint:                  "t.pubmatic.com",
						VideoErrorTrackerEndpoint: "t.pubmatic.com/error",
					},
				},
				cache: nil,
			},
			args: args{
				in0:   context.Background(),
				miCtx: hookstage.ModuleInvocationContext{},
				payload: hookstage.EntrypointPayload{
					Request: func() *http.Request {
						r, err := http.NewRequest("POST", "http://localhost/openrtb/2.5?debug=1", nil)
						if err != nil {
							panic(err)
						}
						r.Header.Add("User-Agent", "go-test")
						r.Header.Add("SOURCE_IP", "127.0.0.1")
						return r
					}(),
					Body: []byte(`{"ext":{"wrapper":{"profileid":5890,"versionid":1,"wiid":"4df09505-d0b2-4d70-94d9-dc41e8e777f7"}}}`),
				},
			},
			want: hookstage.HookResult[hookstage.EntrypointPayload]{
				ModuleContext: hookstage.ModuleContext{
					"rctx": models.RequestCtx{
						ProfileID:                 5890,
						DisplayID:                 1,
						SSAuction:                 -1,
						Debug:                     true,
						UA:                        "go-test",
						IP:                        "127.0.0.1",
						IsCTVRequest:              false,
						TrackerEndpoint:           "t.pubmatic.com",
						VideoErrorTrackerEndpoint: "t.pubmatic.com/error",
						LoggerImpressionID:        "4df09505-d0b2-4d70-94d9-dc41e8e777f7",
						Aliases:                   make(map[string]string),
						ImpBidCtx:                 make(map[string]models.ImpCtx),
						PrebidBidderCode:          make(map[string]string),
						BidderResponseTimeMillis:  make(map[string]int),
					},
				},
			},
			wantErr: nil,
		},
		{
			name: "/openrtb/2.5 request without profileid",
			fields: fields{
				cfg:   config.Config{},
				cache: nil,
			},
			args: args{
				in0:   context.Background(),
				miCtx: hookstage.ModuleInvocationContext{},
				payload: hookstage.EntrypointPayload{
					Request: func() *http.Request {
						r, err := http.NewRequest("POST", "http://localhost/openrtb/2.5", nil)
						if err != nil {
							panic(err)
						}
						return r
					}(),
					Body: []byte(`{"ext":{"wrapper":{"profileids":5890,"versionid":1}}}`),
				},
			},
			want: hookstage.HookResult[hookstage.EntrypointPayload]{
				Reject:  true,
				NbrCode: nbr.InvalidProfileID,
				Errors:  []string{"ErrMissingProfileID"},
			},
			wantErr: nil,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			m := OpenWrap{
				cfg:   tt.fields.cfg,
				cache: tt.fields.cache,
			}
			got, err := m.handleEntrypointHook(tt.args.in0, tt.args.miCtx, tt.args.payload)
			assert.Equal(t, err, tt.wantErr)

			if tt.want.ModuleContext != nil {
				// validate runtime values individually and reset them
				gotRctx := got.ModuleContext["rctx"].(models.RequestCtx)

				assert.NotEmpty(t, gotRctx.StartTime)
				gotRctx.StartTime = 0

				wantRctx := tt.want.ModuleContext["rctx"].(models.RequestCtx)
				if wantRctx.LoggerImpressionID == "" {
					assert.Len(t, gotRctx.LoggerImpressionID, 36)
					gotRctx.LoggerImpressionID = ""
				}

				got.ModuleContext["rctx"] = gotRctx
			}

			assert.Equal(t, got, tt.want)
		})
	}
}
